function result_cell_mod = add_peak_and_trans_to_study_results(result_cell)
% ADD_PEAK_AND_TRANS_TO_STUDY_RESULTS transform_and_peak to result cell
%
% Performs the function transform_and_peak to each result element within
% the cell generated by a study (study_batch_all, study_pwm, ...).
%
% Input:
%   result_cell      cell array, output of study_<...>
%   
% Output:
%   result_cell_mod  same cell array with added content

result_cell_mod = cellfun(@transform_and_peak_to_2nd_ele, result_cell, ...
                           'UniformOutput', false);

end%mainfcn


%% LOCAL FCN

function cell_array_mod  = transform_and_peak_to_2nd_ele(cell_array)
    % Applies the function transform_and_peak to the 2nd element of the
    % 4x1 cell array (input)
    cell_array_mod = cell_array;
    results_mod = transform_and_peak(cell_array{2});
    cell_array_mod{2} = results_mod;
end
